import React from 'react';

const BookCard = ({ book }) => {
  const { volumeInfo } = book;
  const { title, authors, categories, imageLinks } = volumeInfo;

  return (
    <div className="book-card">
      {imageLinks && imageLinks.thumbnail && (
        <img src={imageLinks.thumbnail} alt={title} className="book-image" />
      )}
      <h5>{title}</h5>
      <p>
        <strong>Авторы: </strong>{authors ? authors.join(', ') : 'Неизвестно'}
      </p>
      <p><strong>Категории: </strong>{categories ? categories[0] : 'Не указано'}</p>
    </div>
  );
};

export default BookCard;
