// src/App.js

import React, { useState, useEffect, useCallback } from 'react';
import { fetchBooks } from './utils/api';  // Импортируем функцию fetchBooks
import BookCard from './components/BookCard';
import SearchForm from './components/SearchForm';
import Pagination from './components/Pagination';

function App() {
  const [books, setBooks] = useState([]);
  const [query, setQuery] = useState('');
  const [category, setCategory] = useState('all');
  const [sort, setSort] = useState('relevance');
  const [loading, setLoading] = useState(false);
  const [totalItems, setTotalItems] = useState(0);
  const [startIndex, setStartIndex] = useState(0);
  const [error, setError] = useState(null);

  const handleSearch = useCallback(async () => {
    if (!query) return;

    setLoading(true);
    setError(null);
    try {
      const data = await fetchBooks(query, category, sort, startIndex);
      setBooks((prevBooks) => [...prevBooks, ...data.items]);  // Добавляем новые книги
      setTotalItems(data.totalItems);
      setLoading(false);
    } catch (err) {
      setError(err.message || 'Произошла ошибка при загрузке данных.');
      setLoading(false);
    }
  }, [query, category, sort, startIndex]);

  const loadMoreBooks = () => {
    setStartIndex((prevIndex) => prevIndex + 30);
  };

  useEffect(() => {
    if (query) {
      handleSearch();
    }
  }, [query, category, sort, startIndex, handleSearch]);

  return (
    <div className="container">
      <SearchForm
        query={query}
        setQuery={setQuery}
        category={category}
        setCategory={setCategory}
        sort={sort}
        setSort={setSort}
      />
      {loading && <div>Загрузка...</div>}
      {error && <div className="error">{error}</div>}
      <div>
        <h3>{totalItems} результатов</h3>
        <div className="row">
          {books.map((book) => (
            <div className="col-md-4" key={book.id}>
              <BookCard book={book} />
            </div>
          ))}
        </div>
        {totalItems > books.length && (
          <Pagination onLoadMore={loadMoreBooks} />
        )}
      </div>
    </div>
  );
}

export default App;
