import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import { BrowserRouter as Router, Route, Switch} from "react-router-dom";
import BookCard from "./BookCard";
import SearchForm from "./SearchForm";
import BookDetail from "./BookDetail";
import Pagination from "./Pagination";

const API_KEY = "AIzaSyB-PJvJSqibUZMXVWLz6OYxN6LVXS170hI"; // Подставьте ваш ключ API
const API_URL = "https://www.googleapis.com/books/v1/volumes";

function App() {
  const [books, setBooks] = useState([]);
  const [query, setQuery] = useState("");
  const [category, setCategory] = useState("all");
  const [sort, setSort] = useState("relevance");
  const [loading, setLoading] = useState(false);
  const [totalItems, setTotalItems] = useState(0);
  const [startIndex, setStartIndex] = useState(0);
  const [error, setError] = useState(null);

  const handleSearch = useCallback(async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await axios.get(API_URL, {
        params: {
          q: query,
          filter: category === "all" ? "" : category,
          orderBy: sort,
          startIndex: startIndex,
          maxResults: 30,
          key: API_KEY
        }
      });
      setBooks((prevBooks) => [...prevBooks, ...response.data.items]);
      setTotalItems(response.data.totalItems);
      setLoading(false);
    } catch (err) {
      setError("Произошла ошибка при загрузке данных.");
      setLoading(false);
    }
  }, [query, category, sort, startIndex]);  // Используем useCallback для мемоизации

  const loadMoreBooks = () => {
    setStartIndex(startIndex + 30);
  };

  useEffect(() => {
    if (query) {
      handleSearch();
    }
  }, [query, category, sort, startIndex, handleSearch]);  // Теперь useEffect отслеживает handleSearch

  return (
    <Router>
      <div className="container">
        <SearchForm
          query={query}
          setQuery={setQuery}
          category={category}
          setCategory={setCategory}
          sort={sort}
          setSort={setSort}
        />
        {loading && <div>Загрузка...</div>}
        {error && <div>{error}</div>}
        <div>
          <h3>{totalItems} результатов</h3>
          <div className="row">
            {books.map((book) => (
              <div className="col-md-4" key={book.id}>
                <BookCard book={book} />
              </div>
            ))}
          </div>
          {totalItems > books.length && (
            <Pagination onLoadMore={loadMoreBooks} />
          )}
        </div>
        <Switch>
          <Route path="/book/:id" component={BookDetail} />
        </Switch>
      </div>
    </Router>
  );
}

export default App;
