import React from "react";
import { Link } from "react-router-dom";

const BookCard = ({ book }) => {
  const { volumeInfo } = book;
  const title = volumeInfo.title || "Без названия";
  const authors = volumeInfo.authors ? volumeInfo.authors.join(", ") : "Неизвестный автор";
  const categories = volumeInfo.categories ? volumeInfo.categories[0] : "Без категории";
  const imageUrl = volumeInfo.imageLinks?.thumbnail || "";

  return (
    <div className="card">
      <img src={imageUrl} className="card-img-top" alt={title} />
      <div className="card-body">
        <h5 className="card-title">{title}</h5>
        <p className="card-text">
          <strong>Автор(ы):</strong> {authors}
        </p>
        <p className="card-text">
          <strong>Категория:</strong> {categories}
        </p>
        <Link to={`/book/${book.id}`} className="btn btn-primary">
          Подробнее
        </Link>
      </div>
    </div>
  );
};

export default BookCard;
